import React from 'react';
import "./CartModal.css";

export default function CartModal(props) {
    const { cartItems, onClose, removeFromCart, handleIncrease, handleDecrease, totalAmount } = props;
    return (
        <div className='cart-modal'>
            <div className='cart-modal-header'>
                <p className='shopping-text'>Shopping</p>
                <button type="button" className="close" id='close' data-dismiss="modal" onClick={onClose}>&times;</button>
            </div>
            {cartItems.length > 0 ? (
                cartItems.map((item, index) => (
                    <div>
                        <div key={index} className='cart-item'>
                            <div>
                                <img src={item.imageUrl} alt={item.title} className='cart-item-image'></img>
                            </div>
                            <div className='cart-item-details'>
                                <p className='cart-title'>{item.title}</p>
                                <p className='cart-amount'>£{(item.price * (item.quantity || 1)).toFixed(2)}</p>
                            </div>
                            <div className='trash-btn'>
                                <button onClick={() => removeFromCart(index)} className='delete-btn'>
                                    <i class="fa-regular fa-trash-can"></i>
                                </button>
                            </div>
                        </div>

                    </div>

                ))
            ) : (
                <p>No products available</p>
            )}
            <div className='sub-total'>
                <p className='sub-title'>SUBTOTAL:</p>
                <span className='cart-total-amount'>£ {totalAmount.toFixed(2)}</span>
            </div>
        </div>
    );
}
