import React, { useState, useEffect } from 'react';
import "./Topdeals.css";
import CartModal from '../CartModal/CartModal';

export default function Topdeals(props) {
  const { TopDeals } = props;

  const [cartItems, setCartItems] = useState([]);
  const [isCartOpen, setIsCartOpen] = useState(false);

  const calculateTimeLeft = (daysLeft) => {
    const now = new Date();
    const endTime = new Date(now.getTime() + daysLeft * 24 * 60 * 60 * 1000);
    const timeLeft = endTime - now;

    return {
      days: Math.floor(timeLeft / (1000 * 60 * 60 * 24)),
      hours: Math.floor((timeLeft / (1000 * 60 * 60)) % 24),
      minutes: Math.floor((timeLeft / 1000 / 60) % 60),
      seconds: Math.floor((timeLeft / 1000) % 60),
    };
  };

  const [timeLeft, setTimeLeft] = useState(() => {
    return TopDeals.map((deal) => calculateTimeLeft(deal.daysleft));
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prevTimeLeft) => {
        return prevTimeLeft.map((_, index) => calculateTimeLeft(TopDeals[index].daysleft));
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [TopDeals]);

  const handleAddToCart = (item) => {
    setCartItems((prevItems) => [...prevItems, item]);
  };

  const toggleCartModal = () => {
    co
    setIsCartOpen((prevState) => !prevState);
  };

  return (
    <div className='top-deals col-12 col-lg-12'>
      <h2 className='deals-heading'>Top Deals Of The Day</h2>
      <hr></hr>
      {TopDeals.map((item, index) => (
        <div className='item-card' key={item.id}>
          <div className='discount2'>-{item.discount}%</div>
          <div className='image-container'>
            <img src={item.imageUrl} alt={item.title} className='deal-image'></img>
            <img src={item.imageHoverUrl} alt={item.title} className='deal-image-hover'></img>
          </div>
          <div className='specifications'>
            <h3 className='item-title'>{item.title}</h3>
            <div className='stars'>
              {'★'.repeat(5)}
              <span className='reviews'>({item.review} reviews)</span>
            </div>
            <div className='price'>£ {item.price.toFixed(2)}</div>
            <ul className='specs'>
              <li><span className='dot'>.</span> {item.screen}</li>
              <li><span className='dot'>.</span>{item.os}</li>
              <li><span className='dot'>.</span> {item.inches}</li>
            </ul>
            <div className='countdown d-flex justify-content-around mt-5'>
              <div className='days'>
                {timeLeft[index].days}<br></br> days
              </div>
              <div className='hours'>
                {timeLeft[index].hours}<br></br> hours
              </div>
              <div className='mins'>
                {timeLeft[index].minutes} <br></br>mins
              </div>
              <div className='secs'>
                {timeLeft[index].seconds}<br></br> secs
              </div>
            </div>
            <div className="w3-light-grey">
              <div id="myBar" className="w3-container w3-center" style={{ width: item.sold + '%' }}></div>
            </div>
            <div className='sold'>Sold :<span className='percentage'> {item.sold}/100 </span> Products</div>
            <button className='add-to-cart' onClick={() => handleAddToCart(item)}>Add to Cart</button>
          </div>
        </div>
      ))}
      <div className='cart-div' onClick={toggleCartModal}>
        <i className="fa-solid fa-cart-shopping"></i>
        <span>Your Cart</span>
      </div>
      {isCartOpen && <CartModal cartItems={cartItems} />}
    </div>
  );
}
