import React, { useState, useEffect } from 'react';
import './OnSaleProduct.css';
import { Link } from 'react-router-dom';

export default function OnSaleProduct(props) {
  const { OnSaleProducts, onAddToCart } = props;

  const [startIndex, setStartIndex] = useState(0);
  const [endIndex, setEndIndex] = useState(1);

  // Function to calculate the number of products to display based on screen width
  const calculateProductCount = () => {
    const width = window.innerWidth;
    if (width >= 1024) {
      return 5;
    } else if (width >= 768) {
      return 4;
    } else if (width >= 375) {
      return 2;
    } else if (width >= 320) {
      return 1;
    }
    return 1; // Default
  };

  const updateVisibleProducts = () => {
    const productCount = calculateProductCount();
    setEndIndex(startIndex + productCount);
  };

  useEffect(() => {
    // Update the number of products when the screen size changes
    updateVisibleProducts();
    // Add an event listener to handle screen size changes
    window.addEventListener('resize', updateVisibleProducts);
    return () => {
      window.removeEventListener('resize', updateVisibleProducts);
    };
  }, [startIndex]);

  const handlePrevClick = () => {
    if (startIndex > 0) {
      setStartIndex(startIndex - calculateProductCount());
    }
  };

  const handleNextClick = () => {
    if (endIndex < OnSaleProducts.length) {
      setStartIndex(startIndex + calculateProductCount());
    }
  };

  const OnsaleVisibleProducts = OnSaleProducts.slice(startIndex, endIndex);

  if (!OnSaleProducts || OnSaleProducts.length === 0) {
    return (
      <div className='spinner-loader'>
        <div className='spinner'></div>
      </div>
    );
  }

  return (
    <div className="on-sale-products">
      <div className='onsale-title'>
        <div className='heading-onsale'>
          <h3>Onsale Products</h3>
        </div>
        <div className='types'>
          <a href='#'>Phones & Tablets</a>
          <a href='#'>Computer & Laptop</a>
          <a href='#'>Games & Audio Music</a>
        </div>
      </div>
      <hr />
      <div className='pagination-container'>
        <button
          className='pagination-button prev-button'
          onClick={handlePrevClick}
          disabled={startIndex === 0}
        >
          &lt;
        </button>
        <div className='similar-products-list'>
          {OnsaleVisibleProducts.map((product) => (
            <div className="product-card" key={product._id}>
              <div className='new1'>
                {product.type && <div className="product-type">{product.type}</div>}
                {product.sale && <div className="product-sale">{product.sale}</div>}
                {product.discount && <div className="product-discount">-{product.discount}%</div>}
              </div>
              <div className="image-container1">
                <Link to={`/product/${product._id}`}>
                  <img src={product.imageUrl} alt={product.title} className="product-image" />
                  <img src={product.imageHoverUrl} alt={product.title} className="product-image-hover" />
                </Link>
              </div>
              <Link to={`/product/${product._id}`} className='title-link'>
                <h3 className="product-title">{product.title}</h3>
              </Link>
              <div className="stars">
                {'â˜…'.repeat(5)}
                <span className="reviews">{product.review} reviews</span>
              </div>
              <div className="price">Â£ {product.price.toFixed(2)}</div>
              <button className="add-to-cart" onClick={() => onAddToCart(product)}>Add to Cart</button>
            </div>
          ))}
        </div>
        <button
          className='pagination-button next-button'
          onClick={handleNextClick}
          disabled={endIndex >= OnSaleProducts.length}
        >
          &gt;
        </button>
      </div>
    </div>
  );
}
