import React from 'react'
import Header from '../Header/Header'
import { Link } from 'react-router-dom'
import "./ViewCart.css"

export default function ViewCart(props) {
    const  {cartItems,totalAmount,isModalOpen,removeFromCart,handleAddToCart,toggleCartModal,openModal,closeModal,handleIncrease,handleDecrease} = props
  return (
    <div>
      <Header 
       cartItems={cartItems} 
       totalAmount={totalAmount} 
       isModalOpen={isModalOpen}
       removeFromCart={removeFromCart}
       handleAddToCart={handleAddToCart}
       toggleCartModal={toggleCartModal}
       openModal={openModal}
       closeModal={closeModal}
       handleIncrease={handleIncrease}
       handleDecrease={handleDecrease}
      />
      <div className='Your-cart-div'>
        <h4>Your Cart</h4>
        <div className='return-home-div'>
            <Link to='/'>Home</Link><span className='your-cart-text bg-dark'>/Your Cart</span>
        </div>
      </div>
      <div className='hurry-up-div'>
        <i class="fa-solid fa-circle-exclamation"></i>
        <p className='hurry-up-text'>Please, hurry! Someone has placed an order on one of the items you have in the cart. Products are limited, checkout within <span className='hurry-up-time'>50:12 s</span></p>
      </div>
    </div>
  )
}
