                    </a>
                  </li>
                </ul>
              </div>
            </div>
          </div>

          <div className='hot-line-div'>
            <i className="fa-regular fa-bell"></i>
            Hotline: (+100) 123 456 7890
          </div>
        </div>
      </nav>
    </div>
  );
