import { useState } from "react";
import React from 'react'
import "./LoginModal.css";
import axios from "axios";
import { apiList } from "../../services/apilist";
import Cookie from "js-cookie";

export default function LoginModal({loginBtn,setLoginBtn,setModalOpen}) {


    // const [loginBtn, setLoginBtn] = useState('login');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showSubmiteError, setSubmitError] = useState(false);
    const [errorMsg, setErrorMsg] = useState('');
    const [name, setName] = useState('');
    const [lastName, setLastName] = useState("");

    useEffect(() => {
        if (email || password || name || lastName) {
            const message = error.response?.data?.message || 'Login Error';
                    setErrorMsg(message);
                    if (message === 'User Not Found with this email id') {
                        setLoginBtn('Register');
                    }(false);
            setErrorMsg('');
        }
    }, [email, password, name, lastName]);

    const onSubmitForm = async(e)=>{
        e.preventDefault();

        if(loginBtn === 'login')
        {
            if(email !== '' || password !== '')
            {
                try{
                    const response = await axios.post(apiList.login,{email,password});
                    Cookie.set('authToken', response.data.token, { expires: 1 });
                    alert('Login Successful');
                    console.log(response.data);
                    setModalOpen(false);
                }
                catch(e)
                {
                    const message = e.response?.data?.message || 'Login Error';
                    setErrorMsg(message);
                    if (message === 'User Not Found with this email id') {
                        setLoginBtn('Register');
                    }
                }
            }
            else
            {
                setSubmitError(true);
                setErrorMsg('Please enter reqired fields');
            }
        }
        else
        {
            if(name !== '' || lastName !== '' ||email !== '' || password !== ''){
                if(password.length >=5 && password.length <= 8)
                {
                    try{
                        const response = await axios.post(apiList.signup,{firstName:name,lastName,email,password});
                        alert('Registred Successfully');
                        setLoginBtn("login")
                        setModalOpen(false);
                    }
                    catch(e)
                {
                    setErrorMsg('Registration Error'|| e.response?.data?.message)
                }
                }
                else
                {
                    setErrorMsg('Password should be in between 5 to 8 characters')
                }
                
            }
            else
            {
                setSubmitError(true);
                setErrorMsg('Please enter reqired fields');
            }
        }
    }


    const renderUsername = () => {
        return (
            <>
                <label className='label login' htmlFor='name'>
                    First Name <sup className="astrik">*</sup>
                </label><br></br>
                <input
                    type='text'
                    id='fistname'
                    placeholder='First Name'
                    className='user-input'
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
            </>
        )
    }

    const renderEmail = () => {
        return (
            <>
                <label className='label login' htmlFor='email'>
                    Email Address <sup className="astrik">*</sup>
                </label><br></br>
                <input
                    type='text'
                    id='email'
                    placeholder='Enter Address'
                    className='user-input'
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
            </>
        )
    }
    const renderLastName = () => {
        return (
            <>
                <label className='label login' htmlFor='phoneNumber'>
                    Last Name <sup className="astrik">*</sup>
                </label><br></br>
                <input
                    type='text'
                    id='lastname'
                    placeholder='Last Name'
                    className='user-input'
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                />
            </>
        )
    }
    const renderPassword = () => {
        return (
            <>
                <label className='label login' htmlFor='password'>
                    Password <sup className="astrik">*</sup>
                </label><br></br>
                <input
                    type='text'
                    id='password'
                    placeholder='Enter Your password'
                    className='user-input'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
            </>
        )
    }

    const renderLoginBtn = () => {
        return (
            <>
                <p className="forgot-text">Forgot your password ?</p>
                <button className="login-btn">Login</button>
            </>
        )
    }

    const renderCreateNewBtn = () => {
        return (
            <>
                <button className="register-btn mt-3">CREATE NEW ACCOUNT</button>
            </>
        )
    }

    return (
        <div>
            <div className="modal" id="myModal">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <button type="button" className="close" data-dismiss="modal">&times;</button>
                        <div className="btn-container">
                            <button className={loginBtn === 'login' ? 'login-button' : "login-button btn-off"} onClick={() => setLoginBtn('login')}>Login</button>
                            <button className={loginBtn === 'Register' ? 'login-button' : "login-button btn-off"} onClick={() => setLoginBtn('Register')}>Register</button>
                        </div>
                        <form className='form-container' onSubmit={onSubmitForm}>
                            <div className='input-container'>{loginBtn === 'Register' ? renderUsername() : ''}</div>
                            <div className='input-container'>{loginBtn === 'Register' ? renderLastName() : ''}</div>
                            <div className='input-container'>{renderEmail()}</div>
                            <div className='input-container'>{renderPassword()}</div>
                            <div className='input-container loginBtn'>{loginBtn === 'login' ? renderLoginBtn() : ''}</div>
                            <div className='input-container RegisterBtn'>{loginBtn === 'Register' ? renderCreateNewBtn() : ''}</div>
                        </form>
                        {errorMsg && <p className="error-msg">{errorMsg}</p>}

                        {/* <!-- Modal footer --> */}

                        <div className="modal-footer">
                            <p>New user discount applies only to full price items.<br></br>
                                By providing your email address, you agree to our<br></br>
                                <span className="modal-footer-text">Privacy Policy</span> and <span className="modal-footer-text">Terms of Service</span>
                            </p>
                        </div>

                    </div>
                </div>
            </div>
        </div>
    )
}
