import React,{useState,useEffect} from 'react'

export default function Topdeals(props) {
  const { TopDeals } = props;

  const calculateTimeLeft = (daysLeft) => {
    const difference = daysLeft * 24 * 60 * 60 * 1000;
    const now = new Date();
    const endTime = new Date(now.getTime() + difference);
    const timeLeft = endTime - now;

    const time = {
      days: Math.floor(timeLeft / (1000 * 60 * 60 * 24)),
      hours: Math.floor((timeLeft / (1000 * 60 * 60)) % 24),
      minutes: Math.floor((timeLeft / 1000 / 60) % 60),
      seconds: Math.floor((timeLeft / 1000) % 60),
    };

    return time;
  };

  const [timeLeft, setTimeLeft] = useState(() => {
    return TopDeals.map((deal) => calculateTimeLeft(deal.daysleft));
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(timeLeft.map((_, index) => calculateTimeLeft(TopDeals[index].daysleft)));
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft, TopDeals]);




  return (
    <div className='top-deals'>
      {TopDeals.map((item) => (
        <div className='item-card' key={item.id}>
          <div className='discount2'>-{item.discount}%</div>
          <div className='image-container'>
            <img src={item.imageUrl} alt={item.title} className='deal-image'></img>
            <img src={item.imageHoverUrl} alt={DelayNode.title} className='deal-image-hover'></img>
          </div>
          <div className='specifications'>
            <h3 className='item-title'>{item.title}</h3>
            <div className='stars'>
              {'★'.repeat(5)}
              <span className='reviews'>{item.review} reviews</span>
            </div>
            <div className='price'>{item.price.toFixed(2)}</div>
            <ul className='specs'>
              <li>{item.screen}</li>
              <li>{item.os}</li>
              <li>{item.inches}</li>
            </ul>
            <div className='countdown'>
            {timeLeft[index].days} days {timeLeft[index].hours} hours {timeLeft[index].minutes} mins {timeLeft[index].seconds} secs
            </div>
            <div className='sold'>Sold : {item.sold}/100 Products</div>
            <button className='add-to-cart'>Add to Cart</button>
          </div>
        </div>
      ))}
    </div>
  )
}
