import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import Header from '../Header/Header';
import "./ProductDetails.css";

export default function ProductDetails(props) {
  const { productId } = useParams();
  const { cartItems, totalAmount, isModalOpen, removeFromCart, handleAddToCart, closeModal, handleIncrease, handleDecrease, TopDeals, OnsaleProducts, RecommendedProducts, toggleCartModal, openModal } = props;

  // Combine all products into a single array
  const allProducts = [...TopDeals, ...OnsaleProducts, ...RecommendedProducts];
  const product = allProducts.find(p => p.id === parseInt(productId));

  // Helper functions
  const calculateTimeLeft = (endDate) => {
    const now = new Date();
    const timeLeft = endDate - now;

    return {
      days: Math.floor(timeLeft / (1000 * 60 * 60 * 24)),
      hours: Math.floor((timeLeft / (1000 * 60 * 60)) % 24),
      minutes: Math.floor((timeLeft / 1000 / 60) % 60),
      seconds: Math.floor((timeLeft / 1000) % 60),
    };
  };

  // State management
  const [mainImage, setMainImage] = useState(product ? product.imageUrl : '');
  const [lensStyle, setLensStyle] = useState({ display: 'none' });
  const [endDate] = useState(() => {
    const initialEndDate = new Date();
    initialEndDate.setDate(initialEndDate.getDate() + (product?.daysleft || 0));
    return initialEndDate;
  });
  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft(endDate));
  const [quantity, setQuantity] = useState(1); // Initialize quantity state
  const [activeTab, setActiveTab] = useState('Description');

  // Pagination state
  const [currentPage, setCurrentPage] = useState(0);
  const productsPerPage = 5;

  const handleTabClick = (tabName) => {
    setActiveTab(tabName);
  };

  // Update the countdown timer
  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(calculateTimeLeft(endDate));
    }, 1000);

    return () => clearInterval(timer);
  }, [endDate]);

  const handleImageClick = (imageUrl) => {
    setMainImage(imageUrl);
  };

  const handleMouseMove = (e) => {
    const { left, top, width, height } = e.currentTarget.getBoundingClientRect();
    const lensRadius = 100;
    let x = e.clientX - left;
    let y = e.clientY - top;

    if (x < lensRadius) x = lensRadius;
    if (x > width - lensRadius) x = width - lensRadius;
    if (y < lensRadius) y = lensRadius;
    if (y > height - lensRadius) y = height - lensRadius;

    setLensStyle({
      display: 'block',
      left: `${x - lensRadius}px`,
      top: `${y - lensRadius}px`,
      backgroundPosition: `-${(x * 2) - lensRadius}px -${(y * 2) - lensRadius}px`,
      backgroundSize: `${width * 2}px ${height * 2}px`,
    });
  };

  const handleMouseLeave = () => {
    setLensStyle({ display: 'none' });
  };

  // Handle quantity increase
  const handleQuantityIncrease = () => {
    if (quantity < product.stock) {
      setQuantity(quantity + 1);
    }
  };

  // Handle quantity decrease
  const handleQuantityDecrease = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  // Handle pagination
  const handlePrevPage = () => {
    if (currentPage > 0) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if ((currentPage + 1) * productsPerPage < allProducts.length) {
      setCurrentPage(currentPage + 1);
    }
  };

  const displayedProducts = allProducts.slice(currentPage * productsPerPage, (currentPage + 1) * productsPerPage);

  if (!product) {
    return <div>Product Not Found</div>;
  }

  return (
    <div>
      <Header
        cartItems={cartItems}
        totalAmount={totalAmount}
        isModalOpen={isModalOpen}
        removeFromCart={removeFromCart}
        handleAddToCart={handleAddToCart}
        toggleCartModal={toggleCartModal}
        openModal={openModal}
        closeModal={closeModal}
        handleIncrease={handleIncrease}
        handleDecrease={handleDecrease}
      />
      <div className='product-details-div'>
        <div className='back-to-home'>
          <Link to="/" className='back-to-home-link'><p>Home</p></Link>
          <p className='back-to-home-text text-secondary'>&nbsp; / {product.title}</p>
        </div>
        <hr className='product-details-hr' />
        <div className='product-image-details-div col-lg-12 d-flex'>
          <div className='product-images col-lg-12 d-flex'>
            <div className='display-image col-lg-10'>
              <div
                className='image-zoom-container'
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave}
              >
                <img src={mainImage} className='display-image1' alt={product.title} />
                <div
                  className="zoom-lens"
                  style={{ ...lensStyle, backgroundImage: `url(${mainImage})` }}
                ></div>
              </div>
            </div>
            <div className='display-image-list col-lg-2'>
              <img
                src={product.imageUrl}
                className='display-image2'
                onClick={() => handleImageClick(product.imageUrl)}
                alt={product.title}
              />
              <img
                src={product.imageHoverUrl}
                className='display-image2'
                onClick={() => handleImageClick(product.imageHoverUrl)}
                alt={product.title}
              />
            </div>
          </div>
          <div className='product-details col-lg-6'>
            <h1>{product.title}</h1>
            <div className="stars1">
              {'★'.repeat(5)}
              <span className="reviews1">( {product.review} reviews )</span>
            </div>
            <hr />
            <div className="price1">£ {product.price.toFixed(2)}</div>
            {product.discount && <p>Discount £ {product.discount.toFixed(2)}</p>}
            <ul className='specs'>
              <li><span className='dot'>.</span> {product.screen}</li>
              <li><span className='dot'>.</span> {product.os}</li>
              <li><span className='dot'>.</span> {product.inches}</li>
            </ul>
            <div className='live-view d-flex'>
              <i className="fa-regular fa-eye"></i>
              <p>22 People are viewing this page</p>
            </div>
            <div className='countdown d-flex justify-content-around mt-5'>
              <div>
                <p>Hurry up! Sale ends in:</p>
              </div>
              <div className='d-flex'>
                <div className='days'>
                  {timeLeft.days}<br />days
                </div>
                <div className='hours'>
                  {timeLeft.hours}<br />hours
                </div>
                <div className='mins'>
                  {timeLeft.minutes}<br />mins
                </div>
                <div className='secs'>
                  {timeLeft.seconds}<br />secs
                </div>
              </div>
            </div>
            <div className='stock-left'>
              <p>Hurry Up! Only<span className='text-warning'> {product.stock} </span>Left in Stock!</p>
            </div>
            <div className='add-to-cart-btn-div1 d-flex'>
              <div className='increase-decrease-div d-flex'>
                <button className='decreaseBtn' onClick={handleQuantityDecrease}>-</button>
                <p className='count'>{quantity.toString().padStart(2, '0')}</p>
                <button className='increaseBtn' onClick={handleQuantityIncrease}>+</button>
              </div>
              <div className='add-to-cart-btn'>
                <button className='add-to-cart-btn1' onClick={() => handleAddToCart(product, quantity)}>ADD TO CART</button>
              </div>
            </div>
            <div className='terms d-flex align-items-center mt-4'>
              <input type='checkbox' className='me-2' />
              <p>By clicking this box, I confirm that I have read and agree to the <a href="/">Terms of Service</a></p>
            </div>
            <div className='product-details-footer d-flex mt-4'>
              <button className='review-btn'>Write a Review</button>
              <button className='wishlist-btn'>
                <i className="fa-regular fa-heart"></i> Add to Wishlist
              </button>
            </div>
          </div>
        </div>
        <div className='product-tabs'>
          <div className='tabs-nav'>
            <button
              className={activeTab === 'Description' ? 'active' : ''}
              onClick={() => handleTabClick('Description')}
            >
              Description
            </button>
            <button
              className={activeTab === 'Additional Information' ? 'active' : ''}
              onClick={() => handleTabClick('Additional Information')}
            >
              Additional Information
            </button>
            <button
              className={activeTab === 'Shipping & Return' ? 'active' : ''}
              onClick={() => handleTabClick('Shipping & Return')}
            >
              Shipping & Return
            </button>
            <button
              className={activeTab === 'Reviews' ? 'active' : ''}
              onClick={() => handleTabClick('Reviews')}
            >
              Reviews
            </button>
          </div>
          <div className='description-text'>
            {activeTab === 'Description' && (
              <div>
                <p>{product.description}</p>
              </div>
            )}
            {activeTab === 'Additional Information' && (
              <div>
                <ul>
                  <li><strong>Screen: </strong>{product.screen}</li>
                  <li><strong>OS: </strong>{product.os}</li>
                  <li><strong>Size: </strong>{product.inches} inches</li>
                  <li><strong>Weight: </strong>{product.weight}</li>
                  {/* Add more product-specific details here */}
                </ul>
              </div>
            )}
            {activeTab === 'Shipping & Return' && (
              <div>
                <p>Shipping and return information goes here. For example:</p>
                <ul>
                  <li>Free shipping on orders over £50</li>
                  <li>30-day return policy</li>
                  <li>Contact us for more details about shipping and returns.</li>
                </ul>
              </div>
            )}
            {activeTab === 'Reviews' && (
              <div>
                <p>Customer reviews go here. You can display a list of reviews or a summary of the product's rating.</p>
              </div>
            )}
          </div>
        </div>
        <div className='similar-products'>
          <h2>Similar Products</h2>
          <div className='products-container'>
            {displayedProducts.map((item) => (
              <div key={item.id} className='product-item'>
                <img src={item.imageUrl} alt={item.title} />
                <p>{item.title}</p>
                <p>£ {item.price.toFixed(2)}</p>
                <button onClick={() => handleAddToCart(item, quantity)}>Add to Cart</button>
              </div>
            ))}
          </div>
          <div className='pagination'>
            <button className='pagination-button' onClick={handlePrevPage} disabled={currentPage === 0}>Previous</button>
            <button className='pagination-button' onClick={handleNextPage} disabled={(currentPage + 1) * productsPerPage >= allProducts.length}>Next</button>
          </div>
        </div>
      </div>
    </div>
  );
}
