import React from 'react'
import { Link, useParams } from 'react-router-dom';
// import Topdeals from '../TopDeals/Topdeals';
// import BestSelling from '../BestSelling/BestSelling';
import Header from '../Header/Header';
import "./ProductDetails.css";

export default function ProductDetails(props) {
    const { productId } = useParams();
    const { cartItems, totalAmount, isModalOpen, removeFromCart, handleAddToCart, closeModal, handleIncrease, handleDecrease, TopDeals, OnsaleProducts, ShopByCategory, RecommendedProducts,toggleCartModal,openModal} = props;

    const allProducts = [...TopDeals,...OnsaleProducts,...RecommendedProducts];
    const product = allProducts.find(p=> p.id === parseInt(productId)); 
  return (
    <div>
      <Header
        cartItems={cartItems}
        totalAmount={totalAmount}
        isModalOpen={isModalOpen}
        removeFromCart={removeFromCart}
        handleAddToCart={handleAddToCart}
        toggleCartModal={toggleCartModal}
        openModal={openModal}
        closeModal={closeModal}
        handleIncrease={handleIncrease}
        handleDecrease={handleDecrease}
      />
      {!product ?(
        <div>Product Not Found</div>
      ):(
        
        <div className='product-details-div'>
          <div className='back-to-home'>
            <Link to="/" className='back-to-home-link'><p>Home</p> </Link>
            <p className='back-to-home-text text-secondary'>&nbsp; / {product.title}</p>
          </div>
          <hr className='product-details-hr'></hr>
          <div className='product-image-details-div col-lg-12'>
            <div className='product-images col-lg-12 d-flex'>
              <div className='display-image col-lg-10'>
                <img src={product.imageUrl} className='display-image1 w-75'></img>
              </div>
              <div className='display-image-list col-lg-2'>
                <img src={product.imageUrl} className='display-image1 w-75'></img>
                <img src={product.imageHoverUrl} className='display-image1 w-75'></img>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}
