import React from 'react';
import './Recommended.css';
import { Link } from 'react-router-dom';

export default function Recommended(props) {

    const{RecommendedProducts,onAddToCart}=props;


    
  const [startIndex, setStartIndex] = useState(0);
  const [endIndex, setEndIndex] = useState(5);

  const handlePrevClick = () => {
    if (startIndex > 0) {
      setStartIndex(startIndex - 1);
      setEndIndex(endIndex - 1);
    }
  };

  // Function to handle next button click
  const handleNextClick = () => {
    if (endIndex < OnSaleProducts.length) {
      setStartIndex(startIndex + 1);
      setEndIndex(endIndex + 1);
    }
  };

  const RecommendedVisibleProducts = RecommendedProducts.slice(startIndex, endIndex);

  return (
    <div className="on-sale-products">
        <div className='onsale-title'>
            <div className='heading-onsale'>
                <h3>Recommended Products</h3>
            </div>
            <div className='types'>
                <a href='#'>Screen & Laptop</a>
                <a href='#'>Phones & Tablets</a>
                <a href='#'>Computer & Laptop</a>
            </div>
        </div>
        <hr></hr>
      {RecommendedProducts.map((product) => (
        <div className="product-card" key={product.id}>
          <div className='new1'>
          {product.type && <div className="product-type">{product.type}</div>}
          {product.discount && <div className="product-discount">-{product.discount}%</div>}
          </div>
          <div className="image-container1">
            <Link to={`/product/${product.id}`}><img src={product.imageUrl} alt={product.title} className="product-image" />
            <img src={product.imageHoverUrl} alt={product.title} className="product-image-hover" /></Link>
          </div>
          <Link to={`/product/${product.id}`} className='product-title-link'><h3 className="product-title">{product.title}</h3></Link>
          <div className="stars1">
            {'★'.repeat(5)}
            <span className="reviews1">{product.review} reviews</span>
          </div>
          <div className="price1">£ {product.price.toFixed(2)}</div>
          <button className="add-to-cart1" onClick={() => onAddToCart(product)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
}
