import React from 'react';
import './Recommended.css';

export default function Recommended(props) {

    const{RecommendedProducts,onAddToCart}=props;

  return (
    <div className="on-sale-products">
        <div className='onsale-title'>
            <div className='heading-onsale'>
                <h3>Recommended Products</h3>
            </div>
            <div className='types'>
                <a href='#'>Screen & Laptop</a>
                <a href='#'>Phones & Tablets</a>
                <a href='#'>Computer & Laptop</a>
            </div>
        </div>
        <hr></hr>
      {RecommendedProducts.map((product) => (
        <div className="product-card" key={product.product_id}>
          <div className='new1'>
          {product.product_type && <div className="product-type">{product.product_type}</div>}
          {product.product_discount && <div className="product-discount">{product.product_discount}</div>}
          </div>
          <div className="image-container1">
            <img src={product.product_imageUrl} alt={product.product_title} className="product-image" />
            <img src={product.product_imageHoverUrl} alt={product.product_title} className="product-image-hover" />
          </div>
          <h3 className="product-title">{product.product_title}</h3>
          <div className="stars1">
            {'★'.repeat(5)}
            <span className="reviews1">{product.product_review} reviews</span>
          </div>
          <div className="price1">£ {product.product_price}</div>
          <button className="add-to-cart1" onClick={() => onAddToCart(product)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
}
