import React, { useState, useEffect, useRef } from 'react';
import { Link, useParams } from 'react-router-dom';
import Header from '../Header/Header';
import "./ProductDetails.css";
import Footer from '../Footer/Footer';
import CartModal from '../CartModal/CartModal';

export default function ProductDetails(props) {
  const { productId } = useParams();
  const { cartItems, totalAmount, isModalOpen, removeFromCart, handleAddToCart, closeModal, handleIncrease, handleDecrease, TopDeals, OnsaleProducts, RecommendedProducts, toggleCartModal, openModal } = props;

  // Combine all products into a single array
  const allProducts = [...TopDeals, ...OnsaleProducts, ...RecommendedProducts];
  const product = allProducts.find(p => p.id === parseInt(productId));

  // Helper functions
  const calculateTimeLeft = (endDate) => {
    const now = new Date();
    const timeLeft = endDate - now;

    return {
      days: Math.floor(timeLeft / (1000 * 60 * 60 * 24)),
      hours: Math.floor((timeLeft / (1000 * 60 * 60)) % 24),
      minutes: Math.floor((timeLeft / 1000 / 60) % 60),
      seconds: Math.floor((timeLeft / 1000) % 60),
    };
  };

  // State management
  const [mainImage, setMainImage] = useState(product ? product.imageUrl : '');
  const [lensStyle, setLensStyle] = useState({ display: 'none' });
  const [endDate] = useState(() => {
    const initialEndDate = new Date();
    initialEndDate.setDate(initialEndDate.getDate() + (product?.daysleft || 0));
    return initialEndDate;
  });
  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft(endDate));
  const [quantity, setQuantity] = useState(1); // Initialize quantity state

  const [activeTab, setActiveTab] = useState('Description');

  const handleTabClick = (tabName) => {
    setActiveTab(tabName);
  };


  // Update the countdown timer
  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(calculateTimeLeft(endDate));
    }, 1000);

    return () => clearInterval(timer);
  }, [endDate]);

  const handleImageClick = (imageUrl) => {
    setMainImage(imageUrl);
  };

  useEffect(() => {
    if (product) {
      setMainImage(product.imageUrl);
    }
  }, [product]);
  

  // Determine similar products based on matching any word in the title




  const handleMouseMove = (e) => {
    const { left, top, width, height } = e.currentTarget.getBoundingClientRect();
    const lensRadius = 100;
    let x = e.clientX - left;
    let y = e.clientY - top;

    if (x < lensRadius) x = lensRadius;
    if (x > width - lensRadius) x = width - lensRadius;
    if (y < lensRadius) y = lensRadius;
    if (y > height - lensRadius) y = height - lensRadius;

    setLensStyle({
      display: 'block',
      left: `${x - lensRadius}px`,
      top: `${y - lensRadius}px`,
      backgroundPosition: `-${(x * 2) - lensRadius}px -${(y * 2) - lensRadius}px`,
      backgroundSize: `${width * 2}px ${height * 2}px`,
    });
  };

  const handleMouseLeave = () => {
    setLensStyle({ display: 'none' });
  };

  // Handle quantity increase
  const handleQuantityIncrease = () => {
    if (quantity < product.stock) {
      setQuantity(quantity + 1);
    }
  };

  // Handle quantity decrease
  const handleQuantityDecrease = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const [startIndex, setStartIndex] = useState(0);
  const [endIndex, setEndIndex] = useState(5);

  const doesTitleContainWord = (title, word) => {
    return title.toLowerCase().split(' ').some(t => t.includes(word.toLowerCase()));
  };

  // Filter similar products based on word match in the title
  const similarProducts = allProducts.filter(p => {
    const productTitleWords = product.title.split(' ');
    return productTitleWords.some(word => doesTitleContainWord(p.title, word)) && p.id !== product.id;
  });

  const visibleProducts = similarProducts.slice(startIndex, endIndex);

  // Function to handle prev button click
  const handlePrevClick = () => {
    if (startIndex > 0) {
      setStartIndex(startIndex - 1);
      setEndIndex(endIndex - 1);
    }
  };

  // Function to handle next button click
  const handleNextClick = () => {
    if (endIndex < similarProducts.length) {
      setStartIndex(startIndex + 1);
      setEndIndex(endIndex + 1);
    }
  };



  const [recentlyViewedProducts, setRecentlyViewedProducts] = useState([]);
  const prevProductRef = useRef();

  useEffect(() => {
    if (prevProductRef.current !== product) {
      // Check if the product is already in the recently viewed products array
      if (!recentlyViewedProducts.some(item => item.id === product.id)) {
        setRecentlyViewedProducts((prevItems) => {
          // Add the new product to the beginning of the array
          const updatedItems = [product, ...prevItems];
          // Ensure that only the latest 5 products are kept
          return updatedItems.slice(0, 5);
        });
      }
      prevProductRef.current = product;
    }
  }, [product, recentlyViewedProducts]);





  if (!product) {
    return <div>Product Not Found</div>;
  }

  return (
    <div>
      <Header
        cartItems={cartItems}
        totalAmount={totalAmount}
        isModalOpen={isModalOpen}
        removeFromCart={removeFromCart}
        handleAddToCart={handleAddToCart}
        toggleCartModal={toggleCartModal}
        openModal={openModal}
        closeModal={closeModal}
        handleIncrease={handleIncrease}
        handleDecrease={handleDecrease}
      />
      <div className='product-details-div'>
        <div className='back-to-home'>
          <Link to="/" className='back-to-home-link'><p>Home</p></Link>
          <p className='back-to-home-text text-secondary'>&nbsp; / {product.title}</p>
        </div>
        <hr className='product-details-hr' />
        <div className='product-image-details-div col-lg-12 d-flex'>
          <div className='product-images col-lg-12 d-flex'>
            <div className='display-image col-lg-10'>
              <div
                className='image-zoom-container'
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave}
              >
                <img src={mainImage} className='display-image1' alt={product.title} />
                <div
                  className="zoom-lens"
                  style={{ ...lensStyle, backgroundImage: `url(${mainImage})` }}
                ></div>
              </div>
            </div>
            <div className='display-image-list col-lg-2'>
              <img
                src={product.imageUrl}
                className='display-image2'
                onClick={() => handleImageClick(product.imageUrl)}
                alt={product.title}
              />
              <img
                src={product.imageHoverUrl}
                className='display-image2'
                onClick={() => handleImageClick(product.imageHoverUrl)}
                alt={product.title}
              />
            </div>
          </div>
          <div className='product-details col-lg-6'>
            <h1>{product.title}</h1>
            <div className="stars1">
              {'★'.repeat(5)}
              <span className="reviews1">( {product.review} reviews )</span>
            </div>
            <hr />
            <div className="price1">£ {product.price.toFixed(2)}</div>
            {product.discount && <p>Discount £ {parseInt(product.discount).toFixed(2)}</p>}
            <ul className='specs'>
              <li><span className='dot'>.</span> {product.screen}</li>
              <li><span className='dot'>.</span> {product.os}</li>
              <li><span className='dot'>.</span> {product.inches}</li>
            </ul>
            <div className='live-view d-flex'>
              <i className="fa-regular fa-eye"></i>
              <p>22 People are viewing this page</p>
            </div>
            <div className='countdown d-flex justify-content-around mt-5'>
              <div>
                <p>Hurry up! Sale ends in:</p>
              </div>
              <div className='d-flex'>
                <div className='days'>
                  {timeLeft.days}<br />days
                </div>
                <div className='hours'>
                  {timeLeft.hours}<br />hours
                </div>
                <div className='mins'>
                  {timeLeft.minutes}<br />mins
                </div>
                <div className='secs'>
                  {timeLeft.seconds}<br />secs
                </div>
              </div>
            </div>
            <div className='stock-left'>
              <p>Hurry Up! Only<span className='text-warning'> {product.stock} </span>Left in Stock!</p>
            </div>
            <div className='add-to-cart-btn-div1 d-flex'>
              <div className='increase-decrease-div d-flex'>
                <button className='decreaseBtn' onClick={handleQuantityDecrease}>-</button>
                <p className='count'>{quantity.toString().padStart(2, '0')}</p>
                <button className='increaseBtn' onClick={handleQuantityIncrease}>+</button>
              </div>
              <div className='add-to-cart-btn'>
                <button className='add-to-cart-btn1' onClick={() => handleAddToCart(product)}>ADD TO CART</button>
                {isModalOpen && (
                  <CartModal
                    cartItems={cartItems}
                    onClose={closeModal}
                    removeFromCart={removeFromCart}
                    handleIncrease={handleIncrease}
                    handleDecrease={handleDecrease}
                    totalAmount={totalAmount}
                  />
                )}
              </div>
            </div>
            <div className='terms d-flex align-items-center'>
              <input type='checkbox' className='mr-2'></input>
              <p className='para'>I agree with The Terms & Conditions</p>
            </div>
            <button className='buy-shop-btn1 bg-primary'>Buy with Shop Pay</button>
            <div className='share-div'>
              <div className='wish-add-div'>
                <div className='wish-div1'>
                  <i></i>
                  <p>Add Wishlist</p>
                </div>
                <div className='add-div1'>
                  <i></i>
                  <p>Add compare</p>
                </div>
              </div>
              <div className='share1'>
                <i></i>
                <p>Share</p>
              </div>
            </div>
            <div className='shipping-contact-div'>
              <div className='shipping-return-div'>
                <i></i>
                <p>Shipping and Returns</p>
              </div>
              <div className='Contact-div'>
                <i></i>
                <p>Contact us</p>
              </div>
            </div>
            <div className='estimated-delivery'>
              <i></i>
              <p>Estimated Delivery: {product.delivery && <span className='text-dark'>{product.delivery}</span>}</p>
            </div>
            <div className='return-days'>
              <i></i>
              <p>Return With in <span className='text-dark'> 30 days </span> of purchase. Taxes are non-refundable.</p>
            </div>
            <hr></hr>
            <div className='stock-avalibality d-flex'>
              <div>
                <p>Availability :</p>
                <p>SKU:</p>
                <p>Vendor:</p>
                <p>Categories:</p>
                <p>Tags:</p>
              </div>
              <div>
                <p>{product.avalibility}</p>
                <p>{product.sku}</p>
                <p>{product.vendor}</p>
                <p>{product.categories}</p>
                <p>{product.tags}</p>
              </div>
            </div>
            <div className='gurantee&safe d-flex flex-column justify-content-center align-items-center'>
              <p>Guarantee safe & Secure checkout</p>
              <div className='ecommerce-image'>
                <img className='e-commerce-image' src='https://demo-morata.myshopify.com/cdn/shopifycloud/shopify/assets/payment_icons/amazon-92e856f82cae5a564cd0f70457f11af4d58fa037cf6e5ab7adf76f6fd3b9cafe.svg' alt='image'></img>
                <img className='e-commerce-image' src='https://demo-morata.myshopify.com/cdn/shopifycloud/shopify/assets/payment_icons/apple_pay-f6db0077dc7c325b436ecbdcf254239100b35b70b1663bc7523d7c424901fa09.svg' alt='image'></img>
                <img className='e-commerce-image' src='https://demo-morata.myshopify.com/cdn/shopifycloud/shopify/assets/payment_icons/maestro-d2055c6b416c46cf134f393e1df6e0ba31722b623870f954afd392092207889c.svg' alt='image'></img>
                <img className='e-commerce-image' src='https://demo-morata.myshopify.com/cdn/shopifycloud/shopify/assets/payment_icons/master-173035bc8124581983d4efa50cf8626e8553c2b311353fbf67485f9c1a2b88d1.svg' alt='image'></img>
                <img className='e-commerce-image' src='https://demo-morata.myshopify.com/cdn/shopifycloud/shopify/assets/payment_icons/paypal-49e4c1e03244b6d2de0d270ca0d22dd15da6e92cc7266e93eb43762df5aa355d.svg' alt='image'></img>
                <img className='e-commerce-image' src='https://demo-morata.myshopify.com/cdn/shopifycloud/shopify/assets/payment_icons/shopify_pay-957a48d1202dc65a7890b292de764ee886f7e64cea486ae82e291e9dc824c914.svg' alt='image'></img>
                <img className='e-commerce-image' src='https://demo-morata.myshopify.com/cdn/shopifycloud/shopify/assets/payment_icons/visa-319d545c6fd255c9aad5eeaad21fd6f7f7b4fdbdb1a35ce83b89cca12a187f00.svg' alt='image'></img>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className='tabs-div1 mt-5'>
        <div className='description-div d-flex justify-content-center'>
          {['Description', 'Additional Information', 'Shipping & Return', 'Reviews'].map(tab => (
            <p
              key={tab}
              onClick={() => handleTabClick(tab)}
              className={`tab-heading ${activeTab === tab ? 'active-tab' : ''}`}
            >
              {tab}
            </p>
          ))}
        </div>
        <hr className='product-details-hr1'></hr>
        <div className='description-text'>
          {activeTab === 'Description' && 
          <div className='description-div1'>
            <p>{product.description.title}</p>
            <h2>Work wonders With ease</h2>
            <p>{product.description.workwonders}</p>
            <h2>Product supreme quality</h2>
            <p>{product.description.productQuality}</p>
          </div>}
          {activeTab === 'Additional Information' && 
          <div className='additional-information d-flex'>
            <div className='titles'>
              <p>Color</p>
              <p>Product Type</p>
              <p>Storage</p>
              <p>Brand</p>
              <p>Display</p>
              <p>Capacity</p>
              <p>Chip (CPU)</p>
              <p>Camera and Video</p>
              <p>Front Camera</p>
              <p>Battery Life</p>
              <p>In The Box</p>
              <p>Height</p>
              <p>Width</p>
              <p>Weight</p>
              <p>Mobile Network</p>
            </div>
            <div className='information-div1'>
              <p>{product.additionalInformation.Color}</p>
              <p>{product.additionalInformation.Product_Type}</p>
              <p>{product.additionalInformation.Storage}</p>
              <p>{product.additionalInformation.Brand}</p>
              <p>{product.additionalInformation.Display}</p>
              <p>{product.additionalInformation.Capacity}</p>
              <p>{product.additionalInformation.Chip}</p>
              <p>{product.additionalInformation.Camera}</p>
              <p>{product.additionalInformation.Front_Camera}</p>
              <p>{product.additionalInformation.Color}</p>
              <p>{product.additionalInformation.Color}</p>
              <p>{product.additionalInformation.Color}</p>
              <p>{product.additionalInformation.Color}</p>
              <p>{product.additionalInformation.Color}</p>
              <p>{product.additionalInformation.Color}</p>
            </div>
          </div>}
          {activeTab === 'Shipping & Return' && <p>Shipping & Return information goes here...</p>}
          {activeTab === 'Reviews' && <p>Reviews go here...</p>}
        </div>

      </div>
      <div className='similar-products'>
        <h2>Similar Products</h2>
        <div className='pagination-container'>
          <button
            className='pagination-button prev-button'
            onClick={handlePrevClick}
            disabled={startIndex === 0}
          >
            &lt;
          </button>
          <div className='similar-products-list'>
            {visibleProducts.map((p) => (
              <div key={p.id} className='similar-product-item'>
                <img src={p.imageUrl} alt={p.title} className='similar-image' />
                <div className='similar-product-details'>
                  <h3 className='similar-title'>{p.title}</h3>
                  <p>£ {p.price.toFixed(2)}</p>
                  <Link to={`/product/${p.id}`} className='view-product-link'>View Product</Link>
                </div>
              </div>
            ))}
          </div>
          <button
            className='pagination-button next-button'
            onClick={handleNextClick}
            disabled={endIndex >= similarProducts.length}
          >
            &gt;
          </button>
        </div>
      </div>
      <hr className='product-details-hr1'></hr>
      <div className='recent-products mt-5'>
        <h2 className='recernt-heading pb-3'>Recently Viewed Products</h2>
        <hr className='product-details-hr'></hr>
        <div className='mapping-div'>
          {recentlyViewedProducts.map((product) => (
            <div className="product-card1" key={product.id}>
              <div className='new1'>
                {product.type && <div className="product-type">{product.type}</div>}
                {product.sale && <div className="product-sale">{product.sale}</div>}
                {product.discount && <div className="product-discount">{product.discount}</div>}
              </div>
              <div className="image-container2">
                <Link to={`/product/${product.id}`}><img src={product.imageUrl} alt={product.title} className="product-image1" />
                  <img src={product.imageHoverUrl} alt={product.title} className="product-image-hover1" /></Link>
              </div>
              <Link to={`/product/${product.id}`} className='title-link'><h3 className="product-title1">{product.title}</h3></Link>
              <div className="stars1">
                {'★'.repeat(5)}
                <span className="reviews1">{product.review} reviews</span>
              </div>
              <div className="price1">£ {product.price.toFixed(2)}</div>
              <button className="add-to-cart1" onClick={() => handleAddToCart(product)}>Add to Cart</button>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </div>
  );
}
