import { useState } from "react";
import React from 'react'
import "./LoginModal.css";

export default function LoginModal() {


    const [loginBtn, setLoginBtn] = useState('login');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showSubmiteError, setSubmitError] = useState(false);
    const [errorMsg, setErrorMsg] = useState('');
    const [name, setName] = useState('');
    const [gender, setGender] = useState('');
    const [phoneNumber, setPhoneNumber] = useState("");


    const renderUsername = () => {
        return (
            <>
                <label className='label' htmlFor='name'>
                    Username
                </label>
                <input
                    type='text'
                    id='name'
                    placeholder='Enter Your Name'
                    className='user-input'
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
            </>
        )
    }

    const renderEmail = () => {
        return (
            <>
                <label className='label' htmlFor='email'>
                    Email Address <sup className="astrik">*</sup>
                </label>
                <input
                    type='text'
                    id='email'
                    placeholder='Enter Your Email'
                    className='user-input'
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
            </>
        )
    }
    const renderPhoneNumber = () => {
        return (
            <>
                <label className='label' htmlFor='phoneNumber'>
                    Phone Number
                </label>
                <input
                    type='number'
                    id='phoneNumber'
                    placeholder='Enter Your Phone Number'
                    className='user-input'
                    value={phoneNumber}
                    onChange={(e) => setPhoneNumber(e.target.value)}
                />
            </>
        )
    }
    const renderGender = () => {
        return (
            <>
                <label className='label' htmlFor='gender'>
                    Gender
                </label>
                <input
                    type='text'
                    id='gender'
                    placeholder='Enter Your Gender'
                    className='user-input'
                    value={gender}
                    onChange={(e) => setGender(e.target.value)}
                />
            </>
        )
    }
    const renderPassword = () => {
        return (
            <>
                <label className='label' htmlFor='password'>
                    Password <sup className="astrik">*</sup>
                </label>
                <input
                    type='text'
                    id='password'
                    placeholder='Enter Your password'
                    className='user-input'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
            </>
        )
    }

    return (
        <div>
            <div class="modal" id="myModal">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <div className="btn-container">
                                <button className={loginBtn === 'login' ? 'login-button' : "login-button btn-off"} onClick={() => setLoginBtn('login')}>Login</button>
                                <button className={loginBtn === 'Register' ? 'login-button' : "login-button btn-off"} onClick={() => setLoginBtn('Register')}>Signup</button>
                            </div>
                            <button type="button" class="close" data-dismiss="modal">&times;</button>
                        </div>

                        <div class="modal-body">
                            <form className='form-container'>
                                <div className='input-container'>{loginBtn === 'signup' ? renderUsername() : ''}</div>
                                <div className='input-container'>{renderEmail()}</div>
                                <div className='input-container'>{loginBtn === 'signup' ? renderPhoneNumber() : ''}</div>
                                <div className='input-container'>{loginBtn === 'signup' ? renderGender() : ''}</div>
                                <div className='input-container'>{renderPassword()}</div>

                            </form>
                        </div>

                        {/* <!-- Modal footer --> */}
                        <div class="modal-footer">
                            <button type="button" class="btn btn-danger" data-dismiss="modal">Close</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
