import React from 'react';
import './BestSelling.css';
import { Link } from 'react-router-dom';

export default function BestSelling(props) {

    const{OnSaleProducts,onAddToCart}=props;

  return (
    <div className="on-sale-products">
        <div className='onsale-title'>
            <div className='heading-onsale'>
                <h3>Best Selling TV - Audio</h3>
            </div>
            <div className='types'>
                <a href='#'>Phones & Tablets</a>
                <a href='#'>Computer & Laptop</a>
                <a href='#'>Games & Audio Music</a>
            </div>
        </div>
        <hr></hr>
      {OnSaleProducts.map((product) => (
        <div className="product-card" key={product.id}>
          <div className='new1'>
          {product.type && <div className="product-type">{product.type}</div>}
          {product.sale && <div className="product-sale">{product.sale}</div>}
          {product.discount && <div className="product-discount">{product.discount}</div>}
          </div>
          <div className="image-container1">
            <Link to={`/product/:${product.id}`}><img src={product.imageUrl} alt={product.title} className="product-image" /></Link>
            <img src={product.imageHoverUrl} alt={product.title} className="product-image-hover" />
          </div>
          <Link to={`/product/:${product.id}`}></Link><h3 className="product-title">{product.title}</h3>
          <div className="stars1">
            {'★'.repeat(5)}
            <span className="reviews1">{product.review} reviews</span>
          </div>
          <div className="price1">£ {product.price.toFixed(2)}</div>
          <button className="add-to-cart1" onClick={() => onAddToCart(product)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
}
