import { useState,useEffect } from "react";
import React from 'react'
import "./LoginModal.css";
import axios from "axios";
import { apiList } from "../../services/apilist";
import Cookies from "js-cookie";

export default function LoginModal({ loginBtn, setLoginBtn, closeLoginModal, setLoginModalOpen }) {


    // const [loginBtn, setLoginBtn] = useState('login');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showSubmiteError, setSubmitError] = useState(false);
    const [errorMsg, setErrorMsg] = useState('');
    const [name, setName] = useState('');
    const [lastName, setLastName] = useState("");

    const modalRef = useRef(null);
    const previousActiveElement = useRef(null);

    useEffect(() => {
        if (loginBtn) {
            // Save the currently focused element before opening the modal
            previousActiveElement.current = document.activeElement;
            // Set focus to the modal when it opens
            modalRef.current?.focus();
        } else if (previousActiveElement.current) {
            // Restore focus to the previously focused element after closing the modal
            previousActiveElement.current.focus();
        }
    }, [loginBtn]);

    const onSubmitForm = async (e) => {
        e.preventDefault();

        if (loginBtn === 'login') {
            // Login API logic here
            if (email && password) {
                try {
                    const response = await axios.post(apiList.login,{email,password});
                    Cookies.set("authToken",response.data.token);
                    setErrorMsg("Login Successful");
                    closeLoginModal();
                }
                catch (error) {
                    const message = error.response && error.response.data && error.response.data.message
                        ? error.response.data.message
                        : 'Registration failed. Please try again.';
                    setErrorMsg(message);
                }
            }
            else {
                setErrorMsg('Please fill in all required fields.');
            }
        } else {
            // Signup API logic
            if (name && lastName && email && password) {
                if (password.length >= 5 && password.length <= 8) {
                    try {
                        const response = await axios.post(apiList.signup, { email, password, name, lastName });
                        setErrorMsg('Registered successfully');
                        setLoginBtn('login');
                    } catch (error) {
                        // Check if error response exists and extract message
                        const message = error.response && error.response.data && error.response.data.message
                            ? error.response.data.message
                            : 'Registration failed. Please try again.';
                        setErrorMsg(message);
                    }
                } else {
                    setErrorMsg('Password should be between 5 and 8 characters long.');
                }
            } else {
                setErrorMsg('Please fill in all required fields.');
            }
        }
    };



    const renderUsername = () => {
        return (
            <>
                <label className='label login' htmlFor='name'>
                    First Name <sup className="astrik">*</sup>
                </label><br></br>
                <input
                    type='text'
                    id='fistname'
                    placeholder='First Name'
                    className='user-input'
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
            </>
        )
    }

    const renderEmail = () => {
        return (
            <>
                <label className='label login' htmlFor='email'>
                    Email Address <sup className="astrik">*</sup>
                </label><br></br>
                <input
                    type='text'
                    id='email'
                    placeholder='Enter Address'
                    className='user-input'
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
            </>
        )
    }
    const renderLastName = () => {
        return (
            <>
                <label className='label login' htmlFor='phoneNumber'>
                    Last Name <sup className="astrik">*</sup>
                </label><br></br>
                <input
                    type='text'
                    id='lastname'
                    placeholder='Last Name'
                    className='user-input'
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                />
            </>
        )
    }
    const renderPassword = () => {
        return (
            <>
                <label className='label login' htmlFor='password'>
                    Password <sup className="astrik">*</sup>
                </label><br></br>
                <input
                    type='text'
                    id='password'
                    placeholder='Enter Your password'
                    className='user-input'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
            </>
        )
    }

    const renderLoginBtn = () => {
        return (
            <>
                <p className="forgot-text">Forgot your password ?</p>
                <button className="login-btn">Login</button>
            </>
        )
    }

    const renderCreateNewBtn = () => {
        return (
            <>
                <button className="register-btn mt-3">CREATE NEW ACCOUNT</button>
            </>
        )
    }

    return (
        <div>
            <div  className={`modal ${loginBtn ? 'modal-show' : 'modal-hide'}`} id="myModal">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <button type="button" className="close"  onClick={closeLoginModal}>&times;</button>
                        <div className="btn-container">
                            <button className={loginBtn === 'login' ? 'login-button' : "login-button btn-off"} onClick={() => setLoginBtn('login')}>Login</button>
                            <button className={loginBtn === 'Register' ? 'login-button' : "login-button btn-off"} onClick={() => setLoginBtn('Register')}>Register</button>
                        </div>
                        <form className='form-container' onSubmit={onSubmitForm}>
                            <div className='input-container'>{loginBtn === 'Register' ? renderUsername() : ''}</div>
                            <div className='input-container'>{loginBtn === 'Register' ? renderLastName() : ''}</div>
                            <div className='input-container'>{renderEmail()}</div>
                            <div className='input-container'>{renderPassword()}</div>
                            <div className='input-container loginBtn'>{loginBtn === 'login' ? renderLoginBtn() : ''}</div>
                            <div className='input-container RegisterBtn'>{loginBtn === 'Register' ? renderCreateNewBtn() : ''}</div>
                        </form>
                        {errorMsg && <p className="error-msg">{errorMsg}</p>}

                        {/* <!-- Modal footer --> */}

                        <div className="modal-footer">
                            <p>New user discount applies only to full price items.<br></br>
                                By providing your email address, you agree to our<br></br>
                                <span className="modal-footer-text">Privacy Policy</span> and <span className="modal-footer-text">Terms of Service</span>
                            </p>
                        </div>

                    </div>
                </div>
            </div>
        </div>
    )
}
