import React from 'react';
import './OnSaleProduct.css';
import  {Link}  from 'react-router-dom';
// import CartModal from '../CartModal/CartModal';

export default function OnSaleProduct(props) {

    const{OnSaleProducts,onAddToCart}=props;

    const [startIndex, setStartIndex] = useState(0);
    const [endIndex, setEndIndex] = useState(5);

    const handlePrevClick = () => {
      if (startIndex > 0) {
        setStartIndex(startIndex - 1);
        setEndIndex(endIndex - 1);
      }
    };
  
    // Function to handle next button click
    const handleNextClick = () => {
      if (endIndex < similarProducts.length) {
        setStartIndex(startIndex + 1);
        setEndIndex(endIndex + 1);
      }
    };

    const OnsaleVisibleProducts = OnSaleProducts.slice(startIndex,endIndex);

  return (
    <div className="on-sale-products">
        <div className='onsale-title'>
            <div className='heading-onsale'>
                <h3>Onsale Products</h3>
            </div>
            <div className='types'>
                <a href='#'>Phones & Tablets</a>
                <a href='#'>Computer & Laptop</a>
                <a href='#'>Games & Audio Music</a>
            </div>
        </div>
        <hr></hr>
        <div className='pagination-container'>
          <button
            className='pagination-button prev-button'
            onClick={handlePrevClick}
            disabled={startIndex === 0}
          >
            &lt;
          </button>
          <div className='similar-products-list'>
            {OnsaleVisibleProducts.map((p) => (
              <div className="product-card2" key={p.id}>
              <div className='new1'>
                {p.type && <div className="product-type">{p.type}</div>}
                {p.sale && <div className="product-sale">{p.sale}</div>}
                {p.discount && <div className="product-discount">-{p.discount}%</div>}
              </div>
              <div className="image-container2">
                <Link to={`/product/${p.id}`}><img src={p.imageUrl} alt={p.title} className="product-image1" />
                  <img src={p.imageHoverUrl} alt={p.title} className="product-image-hover1" /></Link>
              </div>
              <Link to={`/product/${p.id}`} className='title-link'><h3 className="product-title1">{p.title}</h3></Link>
              <div className="stars1">
                {'★'.repeat(5)}
                <span className="reviews1">{p.review} reviews</span>
              </div>
              <div className="price1">£ {p.price.toFixed(2)}</div>
              <button className="add-to-cart1" onClick={() => onAddToCart(p)}>Add to Cart</button>
            </div>
            ))}
          </div>
          <button
            className='pagination-button next-button'
            onClick={handleNextClick}
            disabled={endIndex >= OnSaleProducts.length}
          >
            &gt;
          </button>
        </div>
    </div>
  );
}
