  const fetchCartItems = async()=>{
    try{
      const response = await axios.get(apiList.cartItems);
      setCartItems(response.data);
    }catch(error)
    {
      console.error("Failed to fetch cart items : ",error);
    }
  }

  const handleAddToCart = async(newItem) =>{
    try{
      await axios.post(apiList.addToCart,{ ...newItem, quantity: 1 });
      fetchCartItems();
      setModalOpen(true);
      // updateTotalAmount();
    }
    catch(error)
    {
      console.error("Failed to add item to cart : ", error);
    }
  }

  const handleIncrease = async(index)=>{
    try{
      const updatedItem = {...cartItems[index],quantity: cartItems[index].quantity + 1 };
      await axios.post(apiList.addToCart,updatedItem);
      fetchCartItems();
      // updateTotalAmount(updatedItem);
    }catch(error)
    {
      console.error("Failed to increase item quantity : ", error);
    }
  }

  const handleDecrease = async (index) => {
    try {
      const item = cartItems[index];
      if (item.quantity > 1) {
        const updatedItem = { ...item, quantity: item.quantity - 1 };
        await axios.post(apiList.addToCart, updatedItem);
        fetchCartItems(); // Refresh cart items
        updateTotalAmount();
      }
    } catch (error) {
      console.error('Failed to decrease item quantity:', error);
    }
  };


  const removeFromCart = async (index) => {
    try {
      await axios.delete(`http://localhost:5000/api/cart/${cartItems[index]._id}`);
      fetchCartItems(); // Refresh cart items
      updateTotalAmount();
    } catch (error) {
      console.error('Failed to remove item from cart:', error);
    }
  };

