import React from 'react';
import "./CartModal.css";

export default function CartModal(props) {
    const {cartItems,onClose,removeFromCart}=props;
    return (
        <div className='cart-modal'>
            <div>
                <p className='shopping-text'>Shopping</p>
                <button type="button" className="close" data-dismiss="modal" onClick={onClose}>&times;</button>
            </div>
            {cartItems.length > 0 ? (
                cartItems.map((item, index) => (
                    <div key={index} className='cart-item'>
                        <img src={item.imageUrl} alt={item.title} className='cart-item-image'></img>
                        <div className='cart-item-details'>
                            <h3>{item.title}</h3>
                            <p>Â£ {item.price.toFixed(2)}</p>
                            <button onClick={() => removeFromCart(index)} className='delete-btn'>
                                <i className="fa fa-trash"></i> Delete
                            </button>
                        </div>
                    </div>
                ))
            ) : (
                <p>No products available</p>
            )}
        </div>
    );
}
