import React, { useState, useEffect } from 'react';
import './Topdeals.css';

export default function Topdeals({ TopDeals }) {
  const calculateTimeLeft = (daysLeft) => {
    const difference = daysLeft * 24 * 60 * 60 * 1000;
    const now = new Date();
    const endTime = new Date(now.getTime() + difference);
    const timeLeft = endTime - now;

    const time = {
      days: Math.floor(timeLeft / (1000 * 60 * 60 * 24)),
      hours: Math.floor((timeLeft / (1000 * 60 * 60)) % 24),
      minutes: Math.floor((timeLeft / 1000 / 60) % 60),
      seconds: Math.floor((timeLeft / 1000) % 60),
    };

    return time;
  };

  const [timeLeft, setTimeLeft] = useState(() => {
    return TopDeals.map((deal) => calculateTimeLeft(deal.daysleft));
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(timeLeft.map((_, index) => calculateTimeLeft(TopDeals[index].daysleft)));
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft, TopDeals]);

  return (
    <div className="top-deals">
      {TopDeals.map((deal, index) => (
        <div className="deal-card" key={deal.id}>
          <div className="discount">{deal.discount}% OFF</div>
          <div className="image-container">
            <img src={deal.imageUrl} alt={deal.title} className="deal-image" />
            <img src={deal.imageHoverUrl} alt={deal.title} className="deal-image-hover" />
          </div>
          <h3 className="deal-title">{deal.title}</h3>
          <div className="stars">
            {'★'.repeat(5)}
            <span className="reviews">{deal.review} reviews</span>
          </div>
          <div className="price">${deal.price.toFixed(2)}</div>
          <ul className="specifications">
            {Object.values(deal.specifications).map((spec, i) => (
              <li key={i}>• {spec}</li>
            ))}
          </ul>
          <div className="countdown">
            {timeLeft[index].days} days {timeLeft[index].hours} hours {timeLeft[index].minutes} mins {timeLeft[index].seconds} secs
          </div>
          <div className="sold">Sold: {Math.floor(deal.sold / 100)}k</div>
          <button className="add-to-cart">Add to Cart</button>
        </div>
      ))}
    </div>
  );
}
