import { useState } from "react";
import React from 'react'
import "./LoginModal.css";

export default function LoginModal() {


    const [loginBtn, setLoginBtn] = useState('login');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showSubmiteError, setSubmitError] = useState(false);
    const [errorMsg, setErrorMsg] = useState('');
    const [name, setName] = useState('');
    const [lastName, setLastName] = useState("");


    const renderUsername = () => {
        return (
            <>
                <label className='label' htmlFor='name'>
                    First Name <sup className="astrik">*</sup>
                </label>
                <input
                    type='text'
                    id='fistname'
                    placeholder='First Name'
                    className='user-input'
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
            </>
        )
    }

    const renderEmail = () => {
        return (
            <>
                <label className='label' htmlFor='email'>
                    Email Address <sup className="astrik">*</sup>
                </label>
                <input
                    type='text'
                    id='email'
                    placeholder='Enter Your Email'
                    className='user-input'
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
            </>
        )
    }
    const renderLastName = () => {
        return (
            <>
                <label className='label' htmlFor='phoneNumber'>
                   Last Name <sup className="astrik">*</sup>
                </label>
                <input
                    type='text'
                    id='lastname'
                    placeholder='Last Name'
                    className='user-input'
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                />
            </>
        )
    }
    const renderPassword = () => {
        return (
            <>
                <label className='label' htmlFor='password'>
                    Password <sup className="astrik">*</sup>
                </label>
                <input
                    type='text'
                    id='password'
                    placeholder='Enter Your password'
                    className='user-input'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
            </>
        )
    }

    const renderLoginBtn = () =>{
        return (
            <>
                <p className="forgot-text">Forgot your password ?</p>
                <button className="login-btn">Login</button>
            </>
        )
    }

    const renderCreateNewBtn = () =>{
        return (
            <>
                <button className="register-btn">CREATE NEW ACCOUNT</button>
            </>
        )
    }

    return (
        <div>
            <div class="modal" id="myModal">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <div className="btn-container">
                                <button className={loginBtn === 'login' ? 'login-button' : "login-button btn-off"} onClick={() => setLoginBtn('login')}>Login</button>
                                <button className={loginBtn === 'Register' ? 'login-button' : "login-button btn-off"} onClick={() => setLoginBtn('Register')}>Signup</button>
                            </div>
                            <button type="button" class="close" data-dismiss="modal">&times;</button>
                        </div>

                        <div class="modal-body">
                            <form className='form-container'>
                                <div className='input-container'>{loginBtn === 'Register' ? renderUsername() : ''}</div>
                                <div className='input-container'>{loginBtn === 'Register' ? renderLastName() : ''}</div>
                                <div className='input-container'>{renderEmail()}</div>
                                <div className='input-container'>{renderPassword()}</div>
                                <div className='input-container'>{loginBtn === 'login' ? renderLoginBtn() : ''}</div>
                                <div className='input-container'>{loginBtn === 'Register' ? renderCreateNewBtn() : ''}</div>
                            </form>
                        </div>

                        {/* <!-- Modal footer --> */}
                        <div class="modal-footer">
                            <p>New user discount applies only to full price items.<br></br>
By providing your email address, you agree to our
Privacy Policy and Terms of Service.</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
